import React, { useState } from 'react';
import './navbar.css';

const Navbar = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState(null);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const handleOptionClick = (option) => {
        setSelectedOption(option);
        setIsMenuOpen(false); // Close the menu after selecting an option
    };

    return (
        <div>
            <div className='navbar'>
                <div className='logo'>
                    <img src="https://meme-token-gilt.vercel.app/assets/29231-B1Z16x30.png" alt="" />
                    <a>$URANUS</a>
                </div>
                <div className={`dropdown-menu ${isMenuOpen ? 'show' : ''}`}>
                    <button className="menu-option" onClick={() => handleOptionClick('search')}>Search</button>
                    <button className="menu-option" onClick={() => handleOptionClick('design')}>Design by</button>
                </div>
                <div className={`right-section ${selectedOption === 'search' && isMenuOpen ? 'show' : ''}`}>
                    <form className={`form ${selectedOption === 'search' ? 'show' : ''}`}>
                        <input className='input' type="search" placeholder='search' />
                        <button className='submit'>Submit</button>
                    </form>
                </div>
                <div className={`right-section ${selectedOption === 'design' && isMenuOpen ? 'show' : ''}`}>
                    <button className='button' onClick={() => handleOptionClick('design')}>Designed by Suraj Kumar Shaw</button>
                </div>
                <button className="menu-toggle" aria-label="Menu" aria-expanded={isMenuOpen} onClick={toggleMenu}>
                    <span className="line"></span>
                    <span className="line"></span>
                    <span className="line"></span>
                </button>
            </div>
        </div>
    );
};

export default Navbar;
